mapper.insert:
	INSERT INTO BOARD(
					  BOARD_NO
					 ,USER_ID
					 ,CATEGORY_NO
					 ,TITLE
					 ,CONTENT
					 ,BF_ORIGINNAME
					 ,BF_SYSNAME
					 )
	VALUES(
			SEQ_BNO.NEXTVAL
			,#{userId}
			,#{categoryNo}
			,#{title}
			,#{content}
			,#{bfOriginname}
			,#{bfSysname}
			)

mapper.select:
SELECT BOARD_NO ,TITLE ,USER_ID ,CONTENT ,CATEGORY_NO
,ENROLL_DATE
FROM BOARD WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
SET STATUS='N'
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
	SET TITLE = #{title}
	,CONTENT = #{content}
	,BF_ORIGINNAME = #{bfOriginname}
	,BF_SYSNAME = #{bfSysname}
WHERE BOARD_NO = #{boardNo}
