mapper.select:
SELECT COUNT(*)
FROM MATCHING
WHERE USER_NO = #{userNo}
AND SUBJECT = #{subject}
AND STATUS = 'Y'

SELECT M.GROUP_NO 
FROM STUDENT S, 
(SELECT USER_NO,GROUP_NO
FROM MATCHING
WHERE RANDOM = 'Y'

AND GENDER = #{sGender}

AND SUBJECT = #{subject}
AND AREA = #{area}

AND MA_DAY LIKE '%'||#{maDay}||'%'

AND FEE  <=  #{fee}+10000
AND FEE  >=  #{fee}-10000
AND PEOPLE = #{people}
AND CHIEF ='Y'
) M
WHERE S.USER_NO = M.USER_NO
AND YEAR = #{sYear}

	SELECT COUNT(*)
FROM MATCHING
WHERE GROUP_NO = #{groupNo}

mapper.update:
UPDATE MATCHING
SET STATUS ='C'
WHERE GROUP_NO =#{groupNo}

UPDATE MATCHING
SET ALERT ='Y'
WHERE MA_NO = #{maNo}
